import { useState } from 'react';
import './App.css';
import { Header } from './components/Header/Header';

function App() {

  const [userName, setUserName] = useState('');
  const [users, setUsers] = useState(null);

  const searchInput = useRef(null);

  const onEnterPressed = (e) => {
    if(e.keyCode === 13 && userName.trim() !== '') {
      const response = fetch(`https://api.github.com/search/users?q=${userName}`)
      response.then(res => console.log(res.data))
      searchInput.current.blur();
     }
  }

   return (
    <div>
      <Header userName={userName} setUserName={setUserName} onEnterPressed={onEnterPressed} searchInput={searchInput} />
    </div>
  );
}

export default App;
